package dem_Oper;

public class MultiD {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[][]= {{4,5,6},{2,3,1},{6,7,5}};
		int i,j;
		System.out.println("The Array");
		for(i=0;i<3;i++)
		{
			for(j=0;j<3;j++)
			{
					System.out.print(a[i][j]+" ");
			}
			System.out.println();
		}
		
	}

}
